package com.mkpits.datatypes;

public class CharDataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// char is primitive datatype.
		// char is represent single charecter or multiple charecter
		// char datatype default value is ‘\u0000’
		// the memory required for char is 2 byte.
		// range of the char is 0-65535
		// char always mention in single cote ('').
		// this datatype belongs into textual category
		
		char doYouWantToContinue ='y';
		char thisIsAnimal='n';
		char a = 97;
		System.out.println("DO you want to continue :- "+doYouWantToContinue);
		System.out.println("Animal hai ky Ye :-"+thisIsAnimal);
		System.out.println("Charecter :-"+a);

	}

}
